CREATE TABLE IF NOT EXISTS `honorus` (
  `id` int(8) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `sort` tinyint(1) unsigned NOT NULL COMMENT '1学校2省市3国内4国际',
  `name` varchar(50) NOT NULL COMMENT '奖项名称',
  `author` varchar(50) NOT NULL COMMENT '获奖人',
  `img` varchar(255) NOT NULL COMMENT '图片地址',
  `des` text NOT NULL COMMENT '描述',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=9 ;

