CREATE TABLE IF NOT EXISTS `newsus` (
  `id` int(8) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `title` varchar(255) NOT NULL COMMENT '新闻资讯的标题',
  `sort` tinyint(1) unsigned NOT NULL COMMENT '1工作室资讯，2行业资讯，3专业知识',
  `author` varchar(255) NOT NULL COMMENT '作者',
  `time` int(11) unsigned NOT NULL COMMENT '发布的时间',
  `des` text NOT NULL COMMENT '新闻内容',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=7 ;

