--
-- 表的结构 `student`
--

CREATE TABLE IF NOT EXISTS `student` (
  `id` int(8) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL,
  `headurl` varchar(255) NOT NULL,
  `des` text NOT NULL,
  `sort` tinyint(2) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=27 ;

--
-- 转存表中的数据 `student`
--

INSERT INTO `student` (`id`, `name`, `headurl`, `des`, `sort`) VALUES
(1, '唐笑', 'http://localhost/focusVision/public/static/img/student1.jpg', '                        这是唐笑同学的详细描述。即基于PC总线的工业电脑。据2000年IDC统计PC机已占到通用计算机的95%以上，因其价格低、质量高、产量大、软/硬件资源丰富，已被广大的技术人员所熟悉和认可，这正是工业电恼热的基础。其主要的组成部分为工业机箱、无源底板及可插入其上的各种板卡组成，如CPU卡、I/O卡等。并采取全钢机壳、机卡压条过滤网，双正压风扇等设计及EMC（Electro Magnetic Compatibility）技术以解决工业现场的电磁干扰、震动、灰尘、高/低温等问题。IPC有以下特点：\r\n可靠性：工业PC具有在粉尘、烟雾、高/低温、潮湿、震动、腐蚀和快速诊断和可维护性，其MTTR（Mean Time to Repair）一般为10万小时以上。\r\n实时性，工业PC对工业生产过程进行实时在线检测与控制，对工作状况的变化给予快速响应，及时进行采集和输出调节（看门狗功能这是普通PC所不具有的），遇险自复位，保证系统的正常运行。\r\n扩充性，工业PC由于采用底板+CPU卡结构，因而具有很强的输入输出功能，最多可扩充20个板卡，能与工业现场的各种外设、板卡如与道控制器、视频监控系统、车辆检测仪等相连，以完成各种任务。\r\n兼容性，能同时利用ISA与PCI及PICMG资源，并支持各种操作系统，多种语言汇编，多任务操作系统。                ', 1),
(2, '陶思然', 'http://localhost/focusVision/public/static/img/student2.jpg', '这是陶思然同学的详细描述。PLC英文全称ProgrammableLogicController，中文全称为可编程逻辑控制器，定义是：一种数字运算操作的电子系统，专为在工业环境应用而设计的。它采用一类可编程的存储器，用于其内部存储程序，执行逻辑运算，顺序控制，定时，计数与算术操作等面向用户的指令，并通过数字或模拟式输入/输出控制各种类型的机械或生产过程。可编程控制系统（ProgrammableLogicController）是一种专门为在工业环境下应用而设计的数字运算操作电子系统。它采用一种可编程的存储器，在其内部存储执行逻辑运算、顺序控制、定时、计数和算术运算等操作的指令，通过数字式或模拟式的输入输出来控制各种类型的机械设备或生产过程。', 2),
(3, '唐艳', 'http://localhost/focusVision/public/static/img/student3.jpg', '这是唐艳同学的详细描述。是一种高性能、高质量、低成本、配置灵活的分散控制系统系列产品，可以构成各种独立的控制系统、分散控制系统DCS、监控和数据采集系统（SCADA），能满足各种工业领域对过程控制和信息管理的需求。系统的模块化设计、合理的软硬件功能配置和易于扩展的能力，能广泛用于各种大、中、小型电站的分散型控制、发电厂自动化系统的改造以及钢铁、石化、造纸、水泥等工业生产过程控制。', 3),
(4, '顾琳琳', 'http://localhost/focusVision/public/static/img/student4.jpg', '这是顾琳琳同学的详细描述。是全数字串行、双向通信系统。系统内测量和控制设备如探头、激励器和控制器可相互连接、监测和控制。在工厂网络的分级中，它既作为过程控制（如PLC，LC等）和应用智能仪表（如变频器、阀门、条码阅读器等）的局部网，又具有在网络上分布控制应用的内嵌功能。由于其广阔的应用前景，众多国外有实力的厂家竞相投入力量，进行产品开发。国际上已知的现场总线类型有四十余种，比较典型的现场总线有：FF，Profibus，LONworks，CAN，HART，CC-LINK等。现代数控系统是采用微处理器或专用微机的数控系统，由事先存放在存储器里系统程序（软件）来实现控制逻辑，实现部分或全部数控功能，并通过接口与外围设备进行联接，称为计算机数控，简称CNC系统。', 1),
(5, '闫思遥', 'http://localhost/focusVision/public/static/img/student5.jpg', '这是闫思遥同学的详细描述。也叫桌面机，是一种独立相分离的计算机，完完全全跟其它部件无联系，相对于笔记本和上网本体积较大，主机、显示器等设备一般都是相对独立的，一般需要放置在电脑桌或者专门的工作台上。因此命名为台式机。为非常流行的微型计算机，多数人家里和公司用的机器都是台式机。台式机的性能相对较笔记本电脑要强。台式机具有如下特点：\r\n散热性。台式机具有笔记本计算机所无法比拟的优点。台式机的机箱具有空间大、通风条件好的因素而一直被人们广泛使用。', 4),
(6, '赵赫', 'http://localhost/focusVision/public/static/img/student6.jpg', '这是赵赫同学的详细描述。电脑一体机，是由一台显示器、一个电脑键盘和一个鼠标组成的电脑。它的芯片、主板与显示器集成在一起，显示器就是一台电脑，因此只要将键盘和鼠标连接到显示器上，机器就能使用。随着无线技术的发展，电脑一体机的键盘、鼠标与显示器可实现无线链接，机器只有一根电源线。这就解决了一直为人诟病的台式机线缆多而杂的问题。有的电脑一体机还具有电视接收、AV功能，也整合专用软件，可用于特定行业专用机。笔记本电脑可以大体上分为6类：商务型、时尚型、多媒体应用、上网型、学习型、特殊用途。商务型笔记本电脑一般可以概括为移动性强、电池续航时间长、商务软件多；时尚型外观主要针对时尚女性；多媒体应用型笔记本电脑则有较强的图形、图像处理能力和多媒体的能力，尤其是播放能力，为享受型产品。而且，多媒体笔记本电脑多拥有较为强劲的独立显卡和声卡（均支持高清），并有较大的屏幕。上网本（Netbook）就是轻便和低配置的笔记本电脑，具备上网、收发邮件以及即时信息（IM）等功能，并可以实现流畅播放流媒体和音乐。上网本比较强调便携性，多用于在出差、旅游甚至公共交通上的移动上网。学习型机身设计为笔记本外形，采用标准电脑操作，全面整合学习机、电子辞典、复读机、点读机、学生电脑等多种机器功能。特殊用途的笔记本电脑是服务于专业人士，可以在酷暑、严寒、低气压、高海拔、强辐射、战争等恶劣环境下使用的机型，有的较笨重，比如奥运会前期在“华硕珠峰大本营IT服务区”使用的华硕笔记本电脑。', 1),
(7, '屈强', 'http://localhost/focusVision/public/static/img/student7.jpg', '这是屈强同学的详细描述。在掌上电脑基础上加上手机功能，就成了智能手机（Smartphone）。智能手机除了具备手机的通话功能外，还具备了PDA分功能，特别是个人信息管理以及基于无线数据通信的浏览器和电子邮件功能。智能手机为用户提供了足够的屏幕尺寸和带宽，既方便随身携带，又为软件运行和内容服务提供了广阔的舞台，很多增值业务可以就此展开，如股票、新闻、天气、交通、商品、应用程序下载、音乐图片下载等等。平板电脑是一款无须翻盖、没有键盘、大小不等、形状各异，却功能完整的电脑。其构成组件与笔记本电脑基本相同，但它是利用触笔在屏幕上书写，而不是使用键盘和鼠标输入，并且打破了笔记本电脑键盘与屏幕垂直的J 型设计模式。它除了拥有笔记本电脑的所有功能外，还支持手写输入或语音输入，移动性和便携性更胜一筹。 平板电脑由比尔盖茨提出，至少应该是X86架构，从微软提出的平板电脑概念产品上看，平板电脑就是一款无须翻盖、没有键盘、小到足以放入女士手袋，但却功能完整的PC。', 2),
(8, '曲明明', 'http://localhost/focusVision/public/static/img/student8.jpg', '这是曲明明同学的详细描述。嵌入式\r\n即嵌入式系统（ Embedded Systems） ，是一种以应用为中心、以微处理器为基础，软硬件可裁剪的，适应应用系统对功能、可靠性、成本、体积、功耗等综合性严格要求的专用计算机系统。它一般由嵌入式微处理器、外围硬件设备、嵌入式操作系统以及用户的应用程序等四个部分组成。它是计算机市场中增长最快的领域，也是种类繁多，形态多种多样的计算机系统。嵌入式系统几乎包括了生活中的所有电器设备，如掌上pda、计算器、电视机顶盒、手机、数字电视、多媒体播放器、汽车、微波炉、数字相机、家庭自动化系统、电梯、空调、安全系统、自动售货机、蜂窝式电话、消费电子设备、工业自动化仪表与医疗仪器等。', 3),
(9, '姚腾', 'http://localhost/focusVision/public/static/img/student9.jpg', '这是姚腾同学的详细描述。', 2),
(10, '张藤', 'http://localhost/focusVision/public/static/img/student10.jpg', '这是张藤同学的详细描述。信息管理是以数据库管理系统为基础，辅助管理者提高决策水平，改善运营策略的计算机技术。信息处理具体包括数据的采集、存储、加工、分类、排序、检索和发布等一系列工作。信息处理已成为当代计算机的主要任务。是现代化管理的基础。据统计，80%以上的计算机主要应用于信息管理，成为计算机应用的主导方向。信息管理已广泛应用与办公自动化、企事业计算机辅助管理与决策、情报检索、图书馆里、电影电视动画设计、会计电算化等各行各业。', 1),
(11, '严密', 'http://localhost/focusVision/public/static/img/student11.jpg', '这是严密同学的详细描述。过程控制是利用计算机实时采集数据、分析数据，按最优值迅速地对控制对象进行自动调节或自动控制。采用计算机进行过程控制，不仅可以大大提高控制的自动化水平，而且可以提高控制的时效性和准确性，从而改善劳动条件、提高产量及合格率。因此，计算机过程控制已在机械、冶金、石油、化工、电力等部门得到广泛的应用。计算机辅助设计是利用计算机系统辅助设计人员进行工程或产品设计，以实现最佳设计效果的一种技术。CAD技术已应用于飞机设计、船舶设计、建筑设计、机械设计、大规模集成电路设计等。采用计算机辅助设计，可缩短设计时间，提高工作效率，节省人力、物力和财力，更重要的是提高了设计质量。', 1),
(12, '唐笑', 'http://localhost/focusVision/public/static/img/student1.jpg', '这是唐笑同学的详细描述。即基于PC总线的工业电脑。据2000年IDC统计PC机已占到通用计算机的95%以上，因其价格低、质量高、产量大、软/硬件资源丰富，已被广大的技术人员所熟悉和认可，这正是工业电恼热的基础。其主要的组成部分为工业机箱、无源底板及可插入其上的各种板卡组成，如CPU卡、I/O卡等。并采取全钢机壳、机卡压条过滤网，双正压风扇等设计及EMC（Electro Magnetic Compatibility）技术以解决工业现场的电磁干扰、震动、灰尘、高/低温等问题。IPC有以下特点：\r\n可靠性：工业PC具有在粉尘、烟雾、高/低温、潮湿、震动、腐蚀和快速诊断和可维护性，其MTTR（Mean Time to Repair）一般为10万小时以上。\r\n实时性，工业PC对工业生产过程进行实时在线检测与控制，对工作状况的变化给予快速响应，及时进行采集和输出调节（看门狗功能这是普通PC所不具有的），遇险自复位，保证系统的正常运行。\r\n扩充性，工业PC由于采用底板+CPU卡结构，因而具有很强的输入输出功能，最多可扩充20个板卡，能与工业现场的各种外设、板卡如与道控制器、视频监控系统、车辆检测仪等相连，以完成各种任务。\r\n兼容性，能同时利用ISA与PCI及PICMG资源，并支持各种操作系统，多种语言汇编，多任务操作系统。', 1),
(13, '陶思然', 'http://localhost/focusVision/public/static/img/student2.jpg', '这是陶思然同学的详细描述。PLC英文全称ProgrammableLogicController，中文全称为可编程逻辑控制器，定义是：一种数字运算操作的电子系统，专为在工业环境应用而设计的。它采用一类可编程的存储器，用于其内部存储程序，执行逻辑运算，顺序控制，定时，计数与算术操作等面向用户的指令，并通过数字或模拟式输入/输出控制各种类型的机械或生产过程。可编程控制系统（ProgrammableLogicController）是一种专门为在工业环境下应用而设计的数字运算操作电子系统。它采用一种可编程的存储器，在其内部存储执行逻辑运算、顺序控制、定时、计数和算术运算等操作的指令，通过数字式或模拟式的输入输出来控制各种类型的机械设备或生产过程。', 2),
(14, '唐艳', 'http://localhost/focusVision/public/static/img/student3.jpg', '这是唐艳同学的详细描述。是一种高性能、高质量、低成本、配置灵活的分散控制系统系列产品，可以构成各种独立的控制系统、分散控制系统DCS、监控和数据采集系统（SCADA），能满足各种工业领域对过程控制和信息管理的需求。系统的模块化设计、合理的软硬件功能配置和易于扩展的能力，能广泛用于各种大、中、小型电站的分散型控制、发电厂自动化系统的改造以及钢铁、石化、造纸、水泥等工业生产过程控制。', 2),
(15, '顾琳琳', 'http://localhost/focusVision/public/static/img/student4.jpg', '这是顾琳琳同学的详细描述。是全数字串行、双向通信系统。系统内测量和控制设备如探头、激励器和控制器可相互连接、监测和控制。在工厂网络的分级中，它既作为过程控制（如PLC，LC等）和应用智能仪表（如变频器、阀门、条码阅读器等）的局部网，又具有在网络上分布控制应用的内嵌功能。由于其广阔的应用前景，众多国外有实力的厂家竞相投入力量，进行产品开发。国际上已知的现场总线类型有四十余种，比较典型的现场总线有：FF，Profibus，LONworks，CAN，HART，CC-LINK等。现代数控系统是采用微处理器或专用微机的数控系统，由事先存放在存储器里系统程序（软件）来实现控制逻辑，实现部分或全部数控功能，并通过接口与外围设备进行联接，称为计算机数控，简称CNC系统。', 5),
(16, '闫思遥', 'http://localhost/focusVision/public/static/img/student5.jpg', '这是闫思遥同学的详细描述。也叫桌面机，是一种独立相分离的计算机，完完全全跟其它部件无联系，相对于笔记本和上网本体积较大，主机、显示器等设备一般都是相对独立的，一般需要放置在电脑桌或者专门的工作台上。因此命名为台式机。为非常流行的微型计算机，多数人家里和公司用的机器都是台式机。台式机的性能相对较笔记本电脑要强。台式机具有如下特点：\r\n散热性。台式机具有笔记本计算机所无法比拟的优点。台式机的机箱具有空间大、通风条件好的因素而一直被人们广泛使用。', 2),
(17, '赵赫', 'http://localhost/focusVision/public/static/img/student6.jpg', '这是赵赫同学的详细描述。电脑一体机，是由一台显示器、一个电脑键盘和一个鼠标组成的电脑。它的芯片、主板与显示器集成在一起，显示器就是一台电脑，因此只要将键盘和鼠标连接到显示器上，机器就能使用。随着无线技术的发展，电脑一体机的键盘、鼠标与显示器可实现无线链接，机器只有一根电源线。这就解决了一直为人诟病的台式机线缆多而杂的问题。有的电脑一体机还具有电视接收、AV功能，也整合专用软件，可用于特定行业专用机。笔记本电脑可以大体上分为6类：商务型、时尚型、多媒体应用、上网型、学习型、特殊用途。商务型笔记本电脑一般可以概括为移动性强、电池续航时间长、商务软件多；时尚型外观主要针对时尚女性；多媒体应用型笔记本电脑则有较强的图形、图像处理能力和多媒体的能力，尤其是播放能力，为享受型产品。而且，多媒体笔记本电脑多拥有较为强劲的独立显卡和声卡（均支持高清），并有较大的屏幕。上网本（Netbook）就是轻便和低配置的笔记本电脑，具备上网、收发邮件以及即时信息（IM）等功能，并可以实现流畅播放流媒体和音乐。上网本比较强调便携性，多用于在出差、旅游甚至公共交通上的移动上网。学习型机身设计为笔记本外形，采用标准电脑操作，全面整合学习机、电子辞典、复读机、点读机、学生电脑等多种机器功能。特殊用途的笔记本电脑是服务于专业人士，可以在酷暑、严寒、低气压、高海拔、强辐射、战争等恶劣环境下使用的机型，有的较笨重，比如奥运会前期在“华硕珠峰大本营IT服务区”使用的华硕笔记本电脑。', 1),
(18, '屈强', 'http://localhost/focusVision/public/static/img/student7.jpg', '这是屈强同学的详细描述。在掌上电脑基础上加上手机功能，就成了智能手机（Smartphone）。智能手机除了具备手机的通话功能外，还具备了PDA分功能，特别是个人信息管理以及基于无线数据通信的浏览器和电子邮件功能。智能手机为用户提供了足够的屏幕尺寸和带宽，既方便随身携带，又为软件运行和内容服务提供了广阔的舞台，很多增值业务可以就此展开，如股票、新闻、天气、交通、商品、应用程序下载、音乐图片下载等等。平板电脑是一款无须翻盖、没有键盘、大小不等、形状各异，却功能完整的电脑。其构成组件与笔记本电脑基本相同，但它是利用触笔在屏幕上书写，而不是使用键盘和鼠标输入，并且打破了笔记本电脑键盘与屏幕垂直的J 型设计模式。它除了拥有笔记本电脑的所有功能外，还支持手写输入或语音输入，移动性和便携性更胜一筹。 平板电脑由比尔盖茨提出，至少应该是X86架构，从微软提出的平板电脑概念产品上看，平板电脑就是一款无须翻盖、没有键盘、小到足以放入女士手袋，但却功能完整的PC。', 2),
(19, '曲明明', 'http://localhost/focusVision/public/static/img/student8.jpg', '这是曲明明同学的详细描述。嵌入式\r\n即嵌入式系统（ Embedded Systems） ，是一种以应用为中心、以微处理器为基础，软硬件可裁剪的，适应应用系统对功能、可靠性、成本、体积、功耗等综合性严格要求的专用计算机系统。它一般由嵌入式微处理器、外围硬件设备、嵌入式操作系统以及用户的应用程序等四个部分组成。它是计算机市场中增长最快的领域，也是种类繁多，形态多种多样的计算机系统。嵌入式系统几乎包括了生活中的所有电器设备，如掌上pda、计算器、电视机顶盒、手机、数字电视、多媒体播放器、汽车、微波炉、数字相机、家庭自动化系统、电梯、空调、安全系统、自动售货机、蜂窝式电话、消费电子设备、工业自动化仪表与医疗仪器等。', 4),
(20, '姚腾', 'http://localhost/focusVision/public/static/img/student9.jpg', '这是姚腾同学的详细描述。', 3),
(21, '张藤', 'http://localhost/focusVision/public/static/img/student10.jpg', '这是张藤同学的详细描述。信息管理是以数据库管理系统为基础，辅助管理者提高决策水平，改善运营策略的计算机技术。信息处理具体包括数据的采集、存储、加工、分类、排序、检索和发布等一系列工作。信息处理已成为当代计算机的主要任务。是现代化管理的基础。据统计，80%以上的计算机主要应用于信息管理，成为计算机应用的主导方向。信息管理已广泛应用与办公自动化、企事业计算机辅助管理与决策、情报检索、图书馆里、电影电视动画设计、会计电算化等各行各业。', 5),
(22, '严密', 'http://localhost/focusVision/public/static/img/student11.jpg', '这是严密同学的详细描述。过程控制是利用计算机实时采集数据、分析数据，按最优值迅速地对控制对象进行自动调节或自动控制。采用计算机进行过程控制，不仅可以大大提高控制的自动化水平，而且可以提高控制的时效性和准确性，从而改善劳动条件、提高产量及合格率。因此，计算机过程控制已在机械、冶金、石油、化工、电力等部门得到广泛的应用。计算机辅助设计是利用计算机系统辅助设计人员进行工程或产品设计，以实现最佳设计效果的一种技术。CAD技术已应用于飞机设计、船舶设计、建筑设计、机械设计、大规模集成电路设计等。采用计算机辅助设计，可缩短设计时间，提高工作效率，节省人力、物力和财力，更重要的是提高了设计质量。', 1);

